const { knex } = require('@@server/api/utils/knex')

module.exports = ({ models }) => ({
  getPages(trx, {
    limit = 0,
    offset = 0,
  } = {}) {
    return trx
    .from('page')
    .select()
  },

  async getPageById(trx, pageId) {
    const [page] = await trx
    .from('page')
    .select()
    .where({ id: pageId })

    return page
  },

  async createPage(trx, pageProps) {
    const [pageId] = await trx
    .returning('id')
    .into('entity')
    .insert({})

    return trx
    .returning('id')
    .into('page')
    .insert({
      ...pageProps,
      id: pageId,
    })
  },
})
