/**
 * A bunch of modifiers to use with QueryBuilder#modify(fn, ...fnArgs).
 * See: https://knexjs.org/#Builder-modify
 */
Object.assign(exports, {
  /**
   * Optionally applies a limit and offset.
   * @param {KnexQueryBuilder} query  Query Builder!
   * @param {number?} limit  Optional limit.
   * @param {number?} offset Optional offset.
   */
  withLimitOffset(query, limit, offset) {
    if (Number.isFinite(limit) && limit > 0) {
      query.limit(limit)
    }

    if (Number.isFinite(offset) && offset > 0) {
      query.offset(offset)
    }
  },
})
